import React, { useContext, useState } from "react";
import { Card, Form, Button } from "react-bootstrap";
import { UserContext } from "../App";
import { Link, useNavigate } from "react-router-dom";
import userLogo from '.././user.png'

export default function Login() {

    localStorage.setItem("Users", JSON.stringify([{ "user": "admin", "password": "123456", "Tasks": [{"task":"admin t1", "status":"In-progress","assigned":"Pooja"}] }]));
    ;

    const navigate = useNavigate();
    const { user, setUser, pwd, setPwd } = useContext(UserContext);
    const [err, setErr] = useState(false);


    const userList = JSON.parse(localStorage.getItem("Users"));

    const onSubmit = () => {
        console.log(user);
        console.log(pwd);
        const u = userList.find(u => u.user === user);
        if (u && u.password === pwd) {
            setErr(false);
            setUser(u.user);
            navigate('/profile');
        }else{
            setErr(true);
        }
    }

    return (
        <div>
            <Card className="sign-in-card shadow">
                <Card.Body className="m-2">
                    <img src={userLogo} className="logo" />
                    <h4 className="text-center">Sign In</h4>
                    <Form>
                        <Form.Group>
                            {/* <Form.Label className="mb-1">Username</Form.Label> */}
                            {/* <Form.Control className="mb-3" type="text" placeholder="Enter username" required
                                onChange={(e) => setUser(e.target.value)} /> */}
                            {/* <input type="text"
                                placeholder="Enter username"
                                required
                                className="mb-3 center-block"
                                onChange={(e) => setUser(e.target.value)} /> */}
                        </Form.Group>
                        <Form.Group>
                            {/* <Form.Label className="mb-1">Password</Form.Label> */}
                            {/* <Form.Control className="mb-3" type="password" placeholder="Enter password" required
                                onChange={(e) => setPwd(e.target.value)} /> */}
                            {/* <input type="password"
                                placeholder="Enter password"
                                required
                                className="mb-3 center-block"
                                onChange={(e) => setPwd(e.target.value)} /> */}
                        </Form.Group>
                        <div className="center-block">
                            <input type="text"
                                placeholder="Enter username"
                                required
                                className="mb-3 center-block"
                                onChange={(e) => setUser(e.target.value)} />
                            <input type="password"
                                placeholder="Enter password"
                                required
                                className="mb-3 center-block"
                                onChange={(e) => setPwd(e.target.value)} />
                            <div className="text-danger"
                                style={{ visibility: err ? 'visible' : 'hidden' }}>
                                <p>*Incorrect credentials</p>
                            </div>
                        </div>
                        <Button type="button"
                            onClick={onSubmit}
                            className="btn btn-primary rounded"
                            style={{ marginLeft: "36%" }}>Log in</Button>
                    </Form>
                </Card.Body>
            </Card>
        </div>
    )
}
