import React, { useContext } from "react";
import CustomTable from "../components/CustomTable";
import { UserContext } from "../App";
import { PieChart } from "react-minimal-pie-chart";

export default function Home() {

    const { currentTaskList } = useContext(UserContext);

    const data = [
        { title: 'Pending', value: currentTaskList.filter((t) => t.status === 'Pending').length },
        { title: 'Progress', value: currentTaskList.filter((t) => t.status === 'In-progress').length },
        { title: 'Complete', value: currentTaskList.filter((t) => t.status === 'Done').length },
    ];

    return (
        <div className="outlet">
            <div style={{ marginRight: "20px" }}>
                <CustomTable classnames={["table", "shadow", "align-items-center", "w-60"]}
                    data={currentTaskList}
                    headers={["Task", "Status", "Assigned To"]}
                    columns={["task", "status", "assigned"]}>
                </CustomTable>
            </div>
            <div>
                <PieChart
                    animate
                    animationDuration={40}
                    animationEasing="ease-in"
                    center={[50, 50]}
                    data={data}
                    lineWidth={15}
                    lengthAngle={360}
                    paddingAngle={0}
                    radius={50}
                    rounded
                    startAngle={0}
                    viewBoxSize={[100, 100]}
                    labelStyle={{
                        fontSize: "6px",
                        fontColor: "FFFFFA",
                        fontWeight: "500",
                        fontFamily: "monospace"
                    }}
                    label={data => data.dataEntry.title}
                    labelPosition={70}
                />
            </div>
        </div>

    )
}
