import React, { useState, useContext } from "react";
import { Card, Form } from "react-bootstrap";
import { UserContext } from "../App";
import { useNavigate } from "react-router-dom";

export default function Login() {

    localStorage.setItem("Users", JSON.stringify([{ "user": "admin", "password": "123456" }]));

    // const [user, setUser] = useState();
    // const [pwd, setPwd] = useState();
    const navigate = useNavigate();
    const { user, setUser, pwd, setPwd, isSignedIn, setSignedIn } = useContext(UserContext);


    const userList = JSON.parse(localStorage.getItem("Users"));

    const Submit = () => {
        console.log('handle');
        if (userList.find(u => u.user === user)) {
            navigate('/profile');
        }
    }

    return (
        <div>
            <Card className="sign-in-card">
                <Card.Header>
                    <Card.Title>Sign In</Card.Title>
                </Card.Header>
                <Card.Body>
                    <Form onSubmit={Submit()}>
                        <Form.Group>
                            <Form.Label className="mb-1">Username</Form.Label>
                            <Form.Control className="mb-3" type="text" placeholder="Enter username" required
                                onChange={(e) => setUser(e.target.value)} />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label className="mb-1">Password</Form.Label>
                            <Form.Control className="mb-3" type="password" placeholder="Enter password" required
                                onChange={(e) => setPwd(e.target.value)} />
                        </Form.Group>
                        <button className="btn btn-primary justify-content-center" onClick={Submit()}>Sign in</button>
                    </Form>
                </Card.Body>
                <Card.Footer className="align-items-center">
                    <p>Not registered yet? Register here.</p>
                </Card.Footer>
            </Card>
        </div>
    )
}
