import React, { useContext } from "react";
import CustomTable from "../components/CustomTable";
import { UserContext } from "../App";
import { ListGroup, ListGroupItem } from "react-bootstrap";

export default function Home() {

    const { currentTaskList } = useContext(UserContext);

    const assignList = currentTaskList.map((t) => t.assigned)

    return (
        <div className="outlet d-flex">
            <div style={{ marginRight: "20px" }}>
                <CustomTable classnames={["table", "shadow", "align-items-center", "w-60"]}
                    data={currentTaskList}
                    headers={["Task", "Status", "Assigned To"]}
                    columns={["task", "status", "assigned"]}>
                </CustomTable>
            </div>
            <ListGroup className="assignee-list">
                {
                    assignList.map((name) => {
                        console.log(name);
                        let count = currentTaskList.filter((t) => t.assigned === name).length;
                        return <ListGroupItem
                            className="d-flex justify-content-between align-items-center">
                            {name}{count}
                        </ListGroupItem>
                    }
                    )
                }
            </ListGroup>
            {/* <Card className="d-flex mx-auto layout shadow">
                <Card.Body>
                    <Card.Title>To-Do List App</Card.Title>
                    

                </Card.Body>
            </Card> */}
        </div>
    )
}
