import React, { useContext } from "react";
import { Card, Form, Button } from "react-bootstrap";
import { UserContext } from "../App";
import { Link, useNavigate } from "react-router-dom";
import userLogo from '.././user.png'

export default function Login() {

    localStorage.setItem("Users", JSON.stringify([{ "user": "admin", "password": "123456", "Tasks":[]}]));
    const usr = JSON.parse(localStorage.getItem("Users"));  // list of users
    const currentUser = usr.find(t => t.user === user);
    const tasks = currentUser.Tasks;
    console.log(tasks);

    const navigate = useNavigate();
    const { user, setUser, pwd, setPwd } = useContext(UserContext);


    const userList = JSON.parse(localStorage.getItem("Users"));

    const onSubmit = () => {
        console.log(user);
        console.log(pwd);
        if (userList.find(u => u.user === user) && userList.find(u => u.password === pwd)) {
            navigate('/profile');
        }
    }

    return (
        <div>
            <Card className="sign-in-card shadow">
                <Card.Body>
                    <img src={userLogo} className="logo" />
                    <Form>
                        <Form.Group>
                            {/* <Form.Label className="mb-1">Username</Form.Label> */}
                            {/* <Form.Control className="mb-3" type="text" placeholder="Enter username" required
                                onChange={(e) => setUser(e.target.value)} /> */}
                            {/* <input type="text"
                                placeholder="Enter username"
                                required
                                className="mb-3 center-block"
                                onChange={(e) => setUser(e.target.value)} /> */}
                        </Form.Group>
                        <Form.Group>
                            {/* <Form.Label className="mb-1">Password</Form.Label> */}
                            {/* <Form.Control className="mb-3" type="password" placeholder="Enter password" required
                                onChange={(e) => setPwd(e.target.value)} /> */}
                            {/* <input type="password"
                                placeholder="Enter password"
                                required
                                className="mb-3 center-block"
                                onChange={(e) => setPwd(e.target.value)} /> */}
                        </Form.Group>
                        <div className="center-block">
                            <input type="text"
                                placeholder="Enter username"
                                required
                                className="mb-3 center-block"
                                onChange={(e) => setUser(e.target.value)} />
                            <input type="password"
                                placeholder="Enter password"
                                required
                                className="mb-3 center-block"
                                onChange={(e) => setPwd(e.target.value)} />
                            <div className="error"><span>*Incorrect credentials</span></div>
                        </div>
                        <Button type="button"
                            onClick={onSubmit} 
                            className="btn btn-primary rounded"
                            style={{marginLeft:"36%"}}>Log in</Button>
                    </Form>
                </Card.Body>
                <Card.Footer className="align-items-center">
                    <p>Not registered yet? Register <Link to="/register">here</Link></p>
                </Card.Footer>
            </Card>
        </div>
    )
}
