import React, { useContext, useEffect, useState, useRef } from "react";
import { UserContext } from "../App";
import { Form } from "react-bootstrap";
import CustomTable from "../components/CustomTable";
import Loader from "../components/Loader";

export default function Completed() {
    const staticURL = "https://api.postalpincode.in/pincode";

    const [countryData, setCountryData] = useState([]);
    const [zipcode, setZipCode] = useState(110001);
    const [error, setError] = useState(false);
    const [loading, setLoading] = useState(true);
    const zipRef = useRef();

    const handleSearch = () => {
        if (zipRef.current.value.match(/^[0-9]{1,6}$/)) {
            setZipCode(zipRef.current.value);
        } else {
            setError(true);
        }
    }

    useEffect(() => {
        fetch(`${staticURL}/${zipcode}`)
            .then((res) => {
                setLoading(true);
                if (!res.ok) {
                    setError(true);
                }
                setError(false);
                return res.json()
            }) // get response object and convert to JSON
            .then((actualData) => { setCountryData(actualData[0].PostOffice); setLoading(false) })
            .catch() // catch error
    }, [zipcode]);

    return (
        <div className="outlet">
            <div className="d-flex align-items-center m-3">
                <div className="d-flex align-items-center">
                    <label className="m-1">Zipcode</label>
                    <Form.Control type="number" ref={zipRef}></Form.Control>
                    <button
                        className="btn btn-primary m-1"
                        onClick={() => handleSearch()}>
                        Search</button>
                </div>
            </div>
            <Loader loading={loading} />
            {!error && countryData?
                <>
                    <CustomTable classnames={["table", "shadow", "align-items-center", "w-60"]}
                        data={countryData}
                        apiCall={true}
                        headers={["post Office", "Branch Type", "District", "State"]}
                        columns={["Name", "BranchType", "District", "State"]}>
                    </CustomTable>
                </> :
                <h3 className="text-center m-5">Couldn't find data. Check zipcode.</h3>}
        </div>
    )
}
