import React, { useContext, useEffect, useState, useRef } from "react";
import { UserContext } from "../App";
import { Form } from "react-bootstrap";
import CustomTable from "../components/CustomTable";

export default function Completed() {
    const staticURL = "https://api.postalpincode.in/pincode";

    const [countryData, setCountryData] = useState([]);
    const [zipcode, setZipCode] = useState(110001);
    const [error, setError] = useState(false);
    const zipRef = useRef();

    const handleSearch = () => {
        if(zipRef.current.value.match(/^[0-9]{1,6}$/)){
            setZipCode(zipRef.current.value);
        }else{
            setError(true);
        }
    }

    useEffect(() => {
        // fetch("https://api.postalpincode.in/pincode/110001")
        fetch(`${staticURL}/${zipcode}`)
            .then((res) => {
                if (!res.ok) {
                    setError(true);
                }
                setError(false);
                return res.json()
            }) // get response object and convert to JSON
            .then((actualData) => setCountryData(actualData[0].PostOffice))
            .catch(setError(true)) // catch error
    }, [zipcode]);

    return (
        <div className="outlet">
            <div className="d-flex align-items-center m-3">
                <div className="d-flex align-items-center">
                        <label className="m-1">Zipcode</label>
                        <Form.Control type="number" ref={zipRef}></Form.Control>
                        <button className="btn btn-primary" onClick={() => handleSearch()}>Search</button>
                </div>
            </div>

            {!error? <CustomTable classnames={["table", "shadow", "align-items-center", "w-60"]}
                data={countryData}
                apiCall={true}
                headers={["Name", "Branch Type", "District", "State"]}
                columns={["Name", "BranchType", "District", "State"]}>
            </CustomTable>: 
            <h3 className="text-center">Couldn't find data. Check zipcode.</h3>}
        </div>
    )
}
