import React, { useContext, useEffect, useState } from "react";
import { UserContext } from "../App";
import { Form } from "react-bootstrap";
import CustomTable from "../components/CustomTable";

export default function Completed() {

    const { currentTaskList } = useContext(UserContext);
    const [countryData, setCountryData] = useState([]);

    useEffect(() => {
        fetch("http://api.zippopotam.us/IN/590006")
            .then((res) => res.json()) // get response object and convert to JSON
            .then((actualData) => setCountryData(actualData.places))
            .catch(console.log("couldn't fetch data")) // catch error
    }, []);

    return (
        <div className="outlet">
            <Form.Select defaultValue={"Pending"}
                style={{ "marginLeft": "5px", marginRight: "5px", width: "150px" }}
                className="mb-3"
                onChange={(e) => handleSelect(e.target.value)}>
                <option disabled={true} value="">
                    --Select status--
                </option>
                <option value="Pending">Pending</option>
                <option value="In-progress">In-progress</option>
            </Form.Select>

            <CustomTable classnames={["table", "shadow", "align-items-center", "w-60"]}
                data={countryData}
                apiCall={true}
                headers={["Place", "Longitude", "Latitude", "State"]}
                columns={["place name", "longitude", "latitude", "state"]}>
            </CustomTable>
        </div>
    )
}
