import React, { useState } from "react";
import { Card, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

export default function Login() {

    localStorage.setItem("Users", [{"user":"admin", "password":"123456"}]);

    const [user, setUser] = useState();
    const [pwd, setPwd] = useState();

    const Profile = () => {
        const userList = localStorage.getItem("Users");
        if(userList.find(u => u.user===user)){
            useNavigate('/profile');
        }
    }

    return (
        <div>
            <Card className="sign-in-card">
                <Card.Header>
                    <Card.Title>Sign In</Card.Title>
                </Card.Header>
                <Card.Body>
                    <Form onSubmit={handleSubmit()}>
                        <Form.Group>
                            <Form.Label className="mb-1">Username</Form.Label>
                            <Form.Control className="mb-3" type="text" placeholder="Enter username" required
                                onChange={(e) => setUser(e.target.value)} />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label className="mb-1">Password</Form.Label>
                            <Form.Control className="mb-3" type="password" placeholder="Enter password" required
                                onChange={(e) => setPwd(e.target.value)} />
                        </Form.Group>
                        <button className="btn btn-primary justify-content-center">Sign in</button>
                    </Form>
                </Card.Body>
                <Card.Footer className="align-items-center">
                    <p>Not registered yet? Register here.</p>
                </Card.Footer>
            </Card>
        </div>
    )
}
